import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [sequence, setSequence] = useState([]); // Sequência de pares
  const [inputSequence, setInputSequence] = useState([]); // Sequência do usuário
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);

  // Função para gerar nova sessão
  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    try {
      const data = await generateSession(username);
      console.log('Dados recebidos da API:', data); // Log de depuração

      if (data && data.sequence && Array.isArray(data.sequence)) {
        setSessionId(data.session_id);
        setSequence(data.sequence); // Guardando os pares da sequência
        setToken(data.token);
        setIsSessionValid(true);

        // Exibe o token no console
        console.log("Token de Verificação:", data.token);

        toast.success('Sessão gerada com sucesso!');
      } else {
        toast.error('Erro: a sequência não foi retornada corretamente.');
      }
    } catch (error) {
      toast.error('Erro ao gerar sessão');
      console.error('Erro ao gerar sessão:', error); // Log de erro para depuração
    }
  };

  // Função para enviar a sequência e validar no backend
  const handleValidateSequence = async () => {
    try {
      console.log("Sequência do usuário antes do envio:", inputSequence);
  
      // Directly use the inputSequence without wrapping each number
      const response = await axios.post('http://127.0.0.1:8000/validate_sequence', {
        session_id: sessionId,
        sequence: inputSequence,  // Send the sequence directly as a flat array
      }, {
        headers: {
          Authorization: `Bearer ${token}`,  // Token JWT
        },
      });
  
      console.log("✅ Sequência validada com sucesso:", response.data);
      toast.success('Sequência validada com sucesso!');
      setIsSessionValid(false);
      setIsButtonDisabled(true); // Desabilita os botões após a validação
    } catch (error) {
      console.log(error.response ? error.response.data : error.message);  // Log do erro para entender o que aconteceu
      toast.error('Sequência incorreta');
    }
  };
  
  

  // Função para gerar a sessão no backend
  const generateSession = async (username) => {
    const response = await axios.post('http://127.0.0.1:8000/generate_session', { username });
    console.log('Resposta da API de geração de sessão:', response.data); // Log da resposta
    return response.data;
  };

  // Função para lidar com o clique nos botões da sequência
  const handleButtonClick = (number) => {
    setInputSequence((prevSequence) => {
      const newSequence = [...prevSequence, number];
      console.log("Sequência do usuário após clique:", newSequence);
      return newSequence;
    });
  };
  
  

  // Verificação do estado da sequência
  useEffect(() => {
    if (Array.isArray(sequence) && sequence.length > 0) {
      console.log('Sequência carregada:', sequence);
    }
  }, [sequence]);

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>

      <div>
        <label htmlFor="username">Nome de Usuário:</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Digite seu nome de usuário"
        />
      </div>

      <button onClick={handleGenerateSession}>Gerar Nova Sessão</button>

      {isSessionValid ? (
        <>
          <h2>Sequência Gerada:</h2>
          {sequence.length === 0 ? (
            <p>Carregando sequência...</p>
          ) : (
            <ul>
              {sequence.map((pair, index) => (
                <li key={index}>
                  {pair[0]} - {pair[1]}
                </li>
              ))}
            </ul>
          )}

          <h2>Clique na sequência:</h2>
          {sequence.length === 0 ? (
            <p>Esperando sequência...</p>
          ) : (
            <div className="buttons">
              {sequence.map((_, index) => (
                <button
                  key={index}
                  onClick={() => handleButtonClick(index + 1)}
                  disabled={isButtonDisabled} // Desabilita os botões após a validação
                >
                  {index + 1}
                </button>
              ))}
            </div>
          )}

          <h3>Sequência Selecionada:</h3>
          <ul>
            {inputSequence.length === 0 ? (
              <p>Nenhuma sequência selecionada ainda...</p>
            ) : (
              inputSequence.map((num, index) => (
                <li key={index}>{num}</li>
              ))
            )}
          </ul>

          <button onClick={handleValidateSequence}>Validar Sequência</button>

          {/* Exibe o Token de Verificação */}
          <div>
            <h3>Token de Verificação:</h3>
            <p>{token}</p>
          </div>
        </>
      ) : null}

      <ToastContainer />
    </div>
  );
}

export default App;
