import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';
import CryptoJS from 'crypto-js';  // Importe a biblioteca


function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [password, setPassword] = useState([]);
  const [inputSequence, setInputSequence] = useState([]);
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);
  const [buttons, setButtons] = useState([]);

  // Fun√ß√£o para gerar nova sess√£o
  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usu√°rio.');
      return;
    }
  
    try {
      const data = await generateSession(username);
      console.log('Dados recebidos da API:', data);
  
      if (data && data.token) { // Agora recebemos apenas um token
        localStorage.setItem("session_token", data.token); // Armazena o JWT seguro
        setToken(data.token);
  
        // Decodifica a sequ√™ncia do token
        const decodedData = parseJwt(data.token);
        if (decodedData && decodedData.sequence) {
          setPassword(formatSequence(decodedData.sequence));
          generateButtons(decodedData.sequence); // Gera os bot√µes
          setIsSessionValid(true);
  
          toast.success('Sess√£o gerada com sucesso!');
        } else {
          toast.error('Erro ao extrair a sequ√™ncia do token.');
        }
      } else {
        toast.error('Erro: Token n√£o foi retornado corretamente.');
      }
    } catch (error) {
      toast.error('Erro ao gerar sess√£o');
      console.error('Erro ao gerar sess√£o:', error);
    }
  };
  
  // Fun√ß√£o para decodificar o JWT
  const parseJwt = (token) => {
    try {
      return JSON.parse(atob(token.split('.')[1])); // Decodifica o payload do JWT
    } catch (e) {
      console.error("Erro ao decodificar JWT", e);
      return null;
    }
  };

  // Formata a sequ√™ncia correta em pares
  const formatSequence = (sequence) => {
    let formatted = [];
    for (let i = 0; i < sequence.length; i += 2) {
      formatted.push([sequence[i], sequence[i + 1]]);
    }
    return formatted;
  };

  // Fun√ß√£o para validar a sele√ß√£o
  const isValidSelection = (selectedNumber) => {
    const flatPassword = password.flat();  // Aqui "achata" a sequ√™ncia correta da senha
    const nextExpectedNumber = flatPassword[inputSequence.length];  // Verifica o pr√≥ximo n√∫mero esperado
    return selectedNumber === nextExpectedNumber;
  };

  // Fun√ß√£o para lidar com o clique nos bot√µes
  const handleButtonClick = (pair) => {
    const flatPassword = password.flat(); // "Achata" a senha para facilitar a compara√ß√£o
    const nextExpectedNumber = flatPassword[inputSequence.length]; // Obt√©m o pr√≥ximo n√∫mero esperado da senha
    
    // Verifica se o n√∫mero do par √© v√°lido (igual ao pr√≥ximo n√∫mero esperado)
    const isValid = pair.includes(nextExpectedNumber); // Verifica se o pr√≥ximo n√∫mero esperado est√° no par

    if (isValid) {
      // Se for v√°lido, adiciona o n√∫mero √† sequ√™ncia
      setInputSequence((prevSequence) => {
        const newSequence = [...prevSequence, nextExpectedNumber];
        console.log("Sequ√™ncia do usu√°rio ap√≥s clique:", newSequence);
        return newSequence;
      });
    } else {
      // Se for inv√°lido, exibe uma mensagem de erro e n√£o faz nada
      toast.error(`N√∫mero ${nextExpectedNumber} n√£o est√° no par. Tente novamente.`);
    }
  };

  // Gera bot√µes misturados, incluindo as alternativas corretas
  const generateButtons = (sequence) => {
    let correctPairs = [];
    
    // Criar pares corretos com op√ß√µes alternativas
    for (let i = 0; i < sequence.length; i += 2) {
      let num1 = sequence[i];
      let num2 = sequence[i + 1];
      
      // Adiciona cada par como uma op√ß√£o alternada
      correctPairs.push([num1, num2]);
    }

    let allButtons = [];

    // Adiciona os pares corretos
    correctPairs.forEach(pair => {
      allButtons.push(pair);
    });

    // Adiciona pares falsos aleat√≥rios
    for (let i = 0; i < correctPairs.length; i++) {
      let num1 = Math.floor(Math.random() * 10);  // N√∫mero aleat√≥rio entre 0-9
      let num2 = Math.floor(Math.random() * 10);
      allButtons.push([num1, num2]);
    }

    // Embaralha os pares de bot√µes
    allButtons = allButtons.sort(() => Math.random() - 0.5);

    // Define os bot√µes embaralhados no estado
    setButtons(allButtons);
  };

  // Exibe os bot√µes misturados
  const displayButtons = () => {
    if (buttons.length === 0) return <p>Esperando sequ√™ncia...</p>;

    return (
      <div className="buttons">
        {buttons.map((pair, index) => (
          <button
            key={index}
            onClick={() => handleButtonClick(pair)}  // Passa o par inteiro para a fun√ß√£o de clique
            disabled={isButtonDisabled}
          >
            [{pair[0]} ou {pair[1]}]
          </button>
        ))}
      </div>
    );
  };

  // Fun√ß√£o para validar a sequ√™ncia ao final
  const handleValidatePassword = async () => {
    try {
        // Exibe o sessionId para debugar
        console.log("Session ID enviado:", sessionId); // Adicionei esta linha para verificar o sessionId
  
        // Certifique-se de que a sequ√™ncia est√° formatada corretamente
        const formattedSequence = [];
        for (let i = 0; i < inputSequence.length; i += 2) {
            formattedSequence.push([inputSequence[i], inputSequence[i + 1]]);
        }
  
        console.log("Senha do usu√°rio antes do envio:", formattedSequence);
  
        // Verifica se a sequ√™ncia est√° correta
        const isSequenceCorrect = formattedSequence.every((pair, index) => {
            return (
                pair[0] === password[index][0] && pair[1] === password[index][1]
            );
        });
  
        if (!isSequenceCorrect) {
            toast.error('Sequ√™ncia incorreta');
            return;
        }
  
        // üîπ Defini√ß√£o da vari√°vel `data`
        const data = {
            session_id: sessionId,  // Certifique-se de que sessionId est√° armazenado corretamente
            sequence: formattedSequence
        };
  
        const response = await axios.post("http://127.0.0.1:8000/validate_sequence", data, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        });
  
        console.log("‚úÖ Sequ√™ncia validada com sucesso:", response.data);
        toast.success('Sequ√™ncia validada com sucesso!');
        setIsSessionValid(false);
        setIsButtonDisabled(true);
    } catch (error) {
        console.log('Error response:', error.response ? error.response.data : error.message);
        toast.error('Erro ao validar a sequ√™ncia');
    }
  };
  
  // Fun√ß√£o para gerar sess√£o no backend
  const generateSession = async (username) => {
    const response = await axios.post('http://127.0.0.1:8000/generate_session', { username });
    console.log('Resposta da API de gera√ß√£o de sess√£o:', response.data);
    return response.data;
  };

  // Exibe a senha gerada de forma formatada
  const displayPassword = () => {
    if (password.length === 0) return <p>Carregando senha...</p>;

    return (
      <p>
        {password.map(pair => `[${pair[0]},${pair[1]}]`).join(' ')}
      </p>
    );
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>

      <div>
        <label htmlFor="username">Nome de Usu√°rio:</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Digite seu nome de usu√°rio"
        />
      </div>

      <button onClick={handleGenerateSession}>Gerar Nova Sess√£o</button>

      {isSessionValid ? (
        <>
          <h2>Senha Gerada:</h2>
          {displayPassword()}

          <h2>Clique nos bot√µes abaixo para digitar a senha:</h2>
          {displayButtons()}

          <h3>Senha Selecionada:</h3>
          <p>{inputSequence.map(pair => `[${pair[0]},${pair[1]}]`).join(' ') || "Nenhuma sequ√™ncia selecionada ainda..."}</p>

          <button onClick={handleValidatePassword}>Validar Senha</button>

          {/* Exibe o Token de Verifica√ß√£o */}
          <div>
            <h3>Token de Verifica√ß√£o:</h3>
            <p>{token}</p>
          </div>
        </>
      ) : null}

      <ToastContainer />
    </div>
  );
}

export default App;
