import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';
import CryptoJS from 'crypto-js'; // Importe a biblioteca

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [password, setPassword] = useState([]);
  const [inputSequence, setInputSequence] = useState([]);
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);
  const [buttons, setButtons] = useState([]);
  const [isLoading, setIsLoading] = useState(false); // Para controle de loading

  // Fun√ß√£o para gerar hash para cada n√∫mero individualmente
  const hashToNumber = (input) => {
    if (input === undefined || input === null) {
      console.error("Valor inv√°lido para hash:", input);
      return 0; // Retorna 0 ou outro valor padr√£o caso o input seja inv√°lido
    }
  
    const hash = CryptoJS.SHA256(input.toString()).toString(CryptoJS.enc.Base64); // Base64 para garantir um valor √∫nico
    let uniqueNumber = 0;
    
    // Soma os c√≥digos dos caracteres do hash
    for (let i = 0; i < hash.length; i++) {
      uniqueNumber += hash.charCodeAt(i);
    }
  
    // Garante que o n√∫mero gerado esteja no intervalo de 1 a 9
    return (uniqueNumber % 9) + 1;
  };


  // Fun√ß√£o para gerar nova sess√£o
  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usu√°rio.');
      return;
    }

    setIsLoading(true); // Ativa o loading

    try {
      const data = await generateSession(username);
      console.log('Dados recebidos da API:', data);

      if (data && data.sequence && Array.isArray(data.sequence)) {
        setSessionId(data.session_id);
        setPassword(formatSequence(data.sequence)); // Guardando a senha formatada
        setToken(data.token);
        setIsSessionValid(true);
        generateButtons(data.sequence); // Gera bot√µes misturados

        console.log('Token de Verifica√ß√£o:', data.token);
        toast.success('Sess√£o gerada com sucesso!');
      } else {
        toast.error('Erro: a sequ√™ncia n√£o foi retornada corretamente.');
      }
    } catch (error) {
      toast.error('Erro ao gerar sess√£o');
      console.error('Erro ao gerar sess√£o:', error);
    } finally {
      setIsLoading(false); // Desativa o loading
    }
  };

  // Fun√ß√£o para formatar a sequ√™ncia em pares
  const formatSequence = (sequence) => {
    let formatted = [];
    for (let i = 0; i < sequence.length; i += 2) {
      formatted.push([sequence[i], sequence[i + 1]]);
    }
    return formatted;
  };

  // Fun√ß√£o para gerar os bot√µes (agora com hash gerado por n√∫mero individual)
  // Fun√ß√£o para gerar os bot√µes (agora com hash gerado por n√∫mero individual)
  const generateButtons = () => {
    let allButtons = [];
    
    // Gerar n√∫meros aleat√≥rios entre 1 e 9 para os bot√µes
    const randomNumbers = Array.from({ length: 9 }, () => Math.floor(Math.random() * 9) + 1);
  
    // Criar bot√µes com n√∫meros aleat√≥rios
    randomNumbers.forEach(num => {
      allButtons.push({ num });
    });
  
    // Embaralha a ordem dos bot√µes
    allButtons = allButtons.sort(() => Math.random() - 0.5);
  
    // Atualiza o estado com os bot√µes embaralhados
    setButtons(allButtons);
  };
  
  
  // Exibe os bot√µes misturados
  const displayButtons = () => {
  if (buttons.length === 0) return <p>Esperando sequ√™ncia...</p>;

  return (
    <div className="buttons">
      {buttons.map((button, index) => (
        <button
          key={`${button.num}-${index}`} // üî• Adicionando o √≠ndice para garantir unicidade
          onClick={() => handleButtonClick(button.num)}
          disabled={isButtonDisabled}
          className="button"
          style={inputSequence.includes(button.num) ? { backgroundColor: 'green' } : {}}
        >
          {button.secondShortNumber} ou {button.shortNumber}
        </button>
      ))}
    </div>
  );
};
  // Fun√ß√£o para lidar com o clique nos bot√µes
  const handleButtonClick = (pair) => {
    const flatPassword = password.flat(); // "Achata" a senha para facilitar a compara√ß√£o
    const nextExpectedNumber = flatPassword[inputSequence.length]; // Obt√©m o pr√≥ximo n√∫mero esperado da senha

    // Verifica se h√° um pr√≥ximo n√∫mero esperado (caso a senha j√° tenha sido totalmente inserida)
    if (nextExpectedNumber === undefined) {
      toast.error('A senha j√° foi completamente inserida!');
      return;
    }

    // Verifica se ambos os n√∫meros do par s√£o v√°lidos em rela√ß√£o √† sequ√™ncia
    const isValidFirstNumber = pair === nextExpectedNumber;
    const isValidSecondNumber = pair === nextExpectedNumber + 1;

    // Se qualquer um dos dois n√∫meros do par for v√°lido, prossegue
    if (isValidFirstNumber || isValidSecondNumber) {
      setInputSequence((prevSequence) => {
        const newSequence = [...prevSequence, pair];
        console.log('Sequ√™ncia do usu√°rio ap√≥s clique:', newSequence);

        // Verifica se a sequ√™ncia est√° completa e corresponde √† senha
        if (newSequence.length === flatPassword.length) {
          const isCorrect = newSequence.every((num, index) => num === flatPassword[index]);
          if (isCorrect) {
            toast.success('Senha correta! Acesso liberado.');
          } else {
            toast.error('Sequ√™ncia incorreta. Tente novamente.');
            setIsButtonDisabled(true);
            setTimeout(() => setIsButtonDisabled(false), 2000);
          }
        }

        return newSequence;
      });
    } else {
      toast.error(`Par incorreto. O pr√≥ximo n√∫mero esperado √© ${nextExpectedNumber}. Tente novamente.`);
      setIsButtonDisabled(true); // Desabilitar todos os bot√µes ap√≥s erro
      setTimeout(() => setIsButtonDisabled(false), 2000); // Habilitar novamente ap√≥s 2 segundos
    }
  };

  // Exibe a senha gerada com os n√∫meros curtos
  const displayGeneratedPassword = () => {
    if (password.length === 0) return <p>Esperando sequ√™ncia...</p>;

    let generatedPassword = [];
    password.forEach(pair => {
      pair.forEach(num => {
        const shortNumber = hashToNumber(num); // Gerando o n√∫mero curto para cada n√∫mero da senha
        generatedPassword.push(shortNumber);
      });
    });

    return (
      <div>
        <h2>Senha Gerada:</h2>
        <p>{`[${generatedPassword.join(', ')}]`}</p>  {/* Exibe o array plano como uma string */}
      </div>
    );
  };

  // Fun√ß√£o para validar a sequ√™ncia
  const handleValidatePassword = async () => {
    try {
      console.log('Session ID enviado:', sessionId);

      const formattedSequence = password.flat().map((item) => item.toString());
      console.log('Senha do usu√°rio antes do envio:', formattedSequence);

      // Comparando a sequ√™ncia do usu√°rio com a senha correta
      if (inputSequence.join(' ') !== formattedSequence.join(' ')) {
        toast.error('A sequ√™ncia digitada est√° incorreta. Tente novamente.');
        return;
      }

      const data = {
        session_id: sessionId,
        sequence: formattedSequence,
      };

      // Criptografa o sessionId antes de enviar ao backend
      const encryptedSessionId = CryptoJS.AES.encrypt(sessionId, 'chave-secreta').toString();

      const response = await axios.post('http://127.0.0.1:8000/validate_sequence', data, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Encrypted-Session-Id': encryptedSessionId, // Envia sessionId criptografado
        },
      });

      console.log('‚úÖ Sequ√™ncia validada com sucesso:', response.data);
      toast.success('Sequ√™ncia validada com sucesso!');
      setIsSessionValid(false);
    } catch (error) {
      console.log('Error response:', error.response ? error.response.data : error.message);
      toast.error('Erro ao validar a sequ√™ncia');
    }
  };

  // Fun√ß√£o para gerar sess√£o no backend
  const generateSession = async (username) => {
    const response = await axios.post('http://127.0.0.1:8000/generate_session', { username });
    console.log('Resposta da API de gera√ß√£o de sess√£o:', response.data);
    return response.data;
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>

      <div className="form-container">
        <label htmlFor="username">Nome de Usu√°rio:</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Digite seu nome de usu√°rio"
        />
      </div>

      <button onClick={handleGenerateSession} className="primary-button" disabled={isLoading}>
        {isLoading ? 'Gerando Sess√£o...' : 'Gerar Nova Sess√£o'}
      </button>

      {isSessionValid ? (
        <>
          {displayGeneratedPassword()}

          <h2>Clique nos bot√µes abaixo para digitar a senha:</h2>
          <div className="buttons-container">{displayButtons()}</div>

          <h3>Senha Selecionada:</h3>
          <p>{inputSequence.join(' ')}</p>

          <button onClick={handleValidatePassword} className="primary-button">
            Validar Senha
          </button>
        </>
      ) : null}

      <ToastContainer />
    </div>
  );
}

export default App;
