import axios from 'axios';

// Endpoints da API
const API_URL = 'http://127.0.0.1:8000';  // Substitua pelo seu endereço da API

// Função para gerar a sessão
// Função para gerar a sessão
export const generateSession = async (username) => {
  try {
    const response = await axios.post(`${API_URL}/generate_session`, {
      username: username  // Enviando o username para o backend
    });
    return response.data;
  } catch (error) {
    console.error('Erro ao gerar sessão:', error);
    throw error;
  }
};


// Função para validar a sequência
async function validateSequence(sessionId, sequence) {
  // Transformando a sequência em pares (caso necessário)
  const formattedSequence = sequence.map(num => [num]); // Transforma números individuais em pares

  try {
      const response = await axios.post("http://127.0.0.1:8000/validate_sequence", {
          session_id: sessionId,
          sequence: formattedSequence  // Agora enviamos no formato correto!
      }, {
          headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`
          }
      });

      console.log("Resposta do servidor:", response.data);
      return response.data;
  } catch (error) {
      console.error("Erro ao validar sequência:", error);
      throw error;
  }
}

// Função de registro
export const registerUser = async (username, password) => {
  try {
    const response = await fetch('http://127.0.0.1:8000/register', {  // Caminho correto para o registro
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    if (!response.ok) {
      throw new Error('Erro ao registrar usuário');
    }

    const data = await response.json();
    return data;
  } catch (error) {
    throw new Error('Erro ao registrar usuário');
  }
};




// Função para invalidar a sessão
export const invalidateSession = async (sessionId) => {
  try {
    const response = await axios.post(`${API_URL}/invalidate_session`, { session_id: sessionId });
    return response.data;
  } catch (error) {
    console.error('Erro ao invalidar sessão:', error);
    throw error;
  }
};
