import axios from 'axios';

// Endpoints da API
const API_URL = 'http://127.0.0.1:8000';  // Substitua pelo seu endereço da API

// Função para gerar a sessão
export const generateSession = async () => {
  try {
    const response = await axios.post(`${API_URL}/generate_session`);
    return response.data;
  } catch (error) {
    console.error('Erro ao gerar sessão:', error);
    throw error;
  }
};

// Função para validar a sequência
export const validateSequence = async (sessionId, sequence, token) => {
  try {
    const response = await axios.post(`${API_URL}/validate_sequence`, {
      session_id: sessionId,
      sequence: sequence,
    }, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Erro ao validar sequência:', error);
    throw error;
  }
};

export const validateLogin = async (username, password) => {
  try {
    const response = await fetch('URL_DA_API_DE_LOGIN', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    if (!response.ok) {
      throw new Error('Erro no login');
    }

    const data = await response.json();
    return data; // Retorna os dados necessários para o login, como session_id, sequence e token
  } catch (error) {
    throw new Error('Erro no login');
  }
};

// Função para invalidar a sessão
export const invalidateSession = async (sessionId) => {
  try {
    const response = await axios.post(`${API_URL}/invalidate_session`, { session_id: sessionId });
    return response.data;
  } catch (error) {
    console.error('Erro ao invalidar sessão:', error);
    throw error;
  }
};
