import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [password, setPassword] = useState([]); // Agora guardando a senha
  const [inputPassword, setInputPassword] = useState([]); // Sequência de números clicados
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);

  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    try {
      const data = await generateSession(username);
      if (data && data.password && Array.isArray(data.password)) {
        setSessionId(data.session_id);
        setPassword(data.password); // Guardando a senha gerada
        setToken(data.token);
        setIsSessionValid(true);
        toast.success('Sessão gerada com sucesso!');
      } else {
        toast.error('Erro: a senha não foi retornada corretamente.');
      }
    } catch (error) {
      toast.error('Erro ao gerar sessão');
    }
  };

  const handleValidatePassword = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/validate_password', {
        session_id: sessionId,
        password: inputPassword,  // Envia a senha digitada pelo usuário
      }, {
        headers: {
          Authorization: `Bearer ${token}`,  // Token JWT
        },
      });
      toast.success('Senha validada com sucesso!');
      setIsSessionValid(false);
      setIsButtonDisabled(true); // Desabilita os botões após a validação
    } catch (error) {
      toast.error('Senha incorreta');
    }
  };

  const handleButtonClick = (number) => {
    setInputPassword((prevPassword) => {
      const newPassword = [...prevPassword, number];
      return newPassword;
    });
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>

      <div>
        <label htmlFor="username">Nome de Usuário:</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Digite seu nome de usuário"
        />
      </div>

      <button onClick={handleGenerateSession}>Gerar Nova Sessão</button>

      {isSessionValid ? (
        <>
          <h2>Senha Gerada:</h2>
          <ul>
            {password.map((num, index) => (
              <li key={index}>
                {num}
              </li>
            ))}
          </ul>

          <h2>Clique na senha:</h2>
          <div className="buttons">
            {Array.from({ length: 9 }, (_, index) => (
              <button
                key={index}
                onClick={() => handleButtonClick(index + 1)}
                disabled={isButtonDisabled}
              >
                {index + 1}
              </button>
            ))}
          </div>

          <h3>Senha Selecionada:</h3>
          <ul>
            {inputPassword.map((num, index) => (
              <li key={index}>{num}</li>
            ))}
          </ul>

          <button onClick={handleValidatePassword}>Validar Senha</button>

          <div>
            <h3>Token de Verificação:</h3>
            <p>{token}</p>
          </div>
        </>
      ) : null}

      <ToastContainer />
    </div>
  );
}
