import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';
import CryptoJS from 'crypto-js'; // Importe a biblioteca

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [password, setPassword] = useState([]);
  const [inputSequence, setInputSequence] = useState([]);
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);
  const [buttons, setButtons] = useState([]);
  const [isLoading, setIsLoading] = useState(false); // Para controle de loading
  const [hashToNumberMap, setHashToNumberMap] = useState({});

  // Função para converter um hash em um número curto (ex.: entre 1000 e 9999)
  const hashToShortNumber = (input) => {
    // Gera um hash SHA-256 do input
    const hash = CryptoJS.SHA256(input.toString()).toString();
    // Pega os primeiros 8 caracteres do hash e converte para decimal
    const hashPart = parseInt(hash.slice(0, 8), 16);
    // Limita o número ao intervalo 1000-9999
    return 0 + (hashPart % 9); // Garante que o número esteja entre 1000 e 9999
  };

  par1 = hashToShortNumber(button.pair[0])
  par2 = hashToShortNumber(button.pair[0])
  const hashToTwoNumbers = (input) => {
    // Gera um hash SHA-256 do input
    const hash = CryptoJS.SHA256(input.toString()).toString();
    
    // Pega diferentes partes do hash para garantir exclusividade
    const num1 = parseInt(hash.slice(0, 8), 16) % 10; // Primeiro número
    const num2 = parseInt(hash.slice(8, 16), 16) % 10; // Segundo número
    
    // Certifica-se de que num1 e num2 são sempre exclusivos para esse par
    return [num1, num2];
  };
  // Função para gerar nova sessão
  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    try {
      const data = await generateSession(username);
      console.log('Dados recebidos da API:', data);

      if (data && data.sequence && Array.isArray(data.sequence)) {
        setSessionId(data.session_id);
        setPassword(formatSequence(data.sequence)); // Guardando a senha formatada
        setToken(data.token);
        setIsSessionValid(true);
        generateButtons(data.sequence); // Gera botões com números misturados

        console.log("Token de Verificação:", data.token);
        toast.success('Sessão gerada com sucesso!');
      } else {
        toast.error('Erro: a sequência não foi retornada corretamente.');
      }
    } catch (error) {
      toast.error('Erro ao gerar sessão');
      console.error('Erro ao gerar sessão:', error);
    }
  };

  // Função para formatar a sequência em pares
  const formatSequence = (sequence) => {
    let formatted = [];
    for (let i = 0; i < sequence.length; i += 2) {
      formatted.push([sequence[i], sequence[i + 1]]);
    }
    return formatted;
  };


  // Função para gerar os botões (misturados)
 // Função para gerar os botões (misturados)
 const generateButtons = (sequence) => {
  let correctPairs = [];

  // Criar pares corretos com opções alternativas
  for (let i = 0; i < sequence.length; i += 2) {
    let num1 = sequence[i];
    let num2 = sequence[i + 1];
    
    // Adiciona cada par como uma opção alternada
    correctPairs.push([num1, num2]);
  }

  let allButtons = [];

  // Adiciona os pares corretos
  correctPairs.forEach(pair => {
    allButtons.push({ pair, isCorrect: true, shortNumbers: [hashToShortNumber(pair[0]), hashToShortNumber(pair[1])] });
  });

  // Adiciona pares falsos aleatórios
  for (let i = 0; i < correctPairs.length; i++) {
    let num1 = Math.floor(Math.random() * 10);  // Número aleatório entre 0-9
    let num2 = Math.floor(Math.random() * 10);
    allButtons.push({ pair: [num1, num2], isCorrect: false, shortNumbers: [hashToShortNumber(num1), hashToShortNumber(num2)] });
  }

  // Embaralha os pares de botões
  allButtons = allButtons.sort(() => Math.random() - 0.5);

  // Define os botões embaralhados no estado
  setButtons(allButtons);
};


  // Exibe os botões misturados
  const displayButtons = () => {
    if (buttons.length === 0) return <p>Esperando sequência...</p>;
  
    return (
      <div className="buttons">
        {buttons.map((button, index) => (
          <button
            key={index}
            onClick={() => handleButtonClick(button.pair)} // Passa o par inteiro para a função de clique
            disabled={isButtonDisabled}
          >
            [{hashToShortNumber(button.pair[0])} ou {hashToShortNumber(button.pair[1])}]
          </button>
        ))}
      </div>
    );
  };
  

  // Exibe a senha gerada de forma formatada
  const displayPassword = () => {
    if (password.length === 0) return <p>Carregando senha...</p>;

    return (
      <p>
        {password.map(pair => `[${pair[0]},${pair[1]}]`).join(' ')}
      </p>
    );
  };
  // Função para lidar com o clique nos botões
  const handleButtonClick = (pair) => {
    const flatPassword = password.flat(); 
    const nextExpectedNumber = flatPassword[inputSequence.length]; 
    
    console.log("Número esperado:", nextExpectedNumber);
    console.log("Par selecionado:", pair);
  
    const hash = pair.join(','); // Cria um hash do par de números, se pair for um array
    const uniqueNumber = getUniqueNumberForHash(hash); 
  
    console.log("Número gerado para o par:", uniqueNumber);
    
    const isValid = uniqueNumber === nextExpectedNumber;
  
    if (isValid) {
      setInputSequence((prevSequence) => {
        const newSequence = [...prevSequence, nextExpectedNumber];
        console.log("Sequência do usuário após clique:", newSequence);
        return newSequence;
      });
    } else {
      toast.error(`Número ${nextExpectedNumber} não está no par. Tente novamente.`);
    }
  };
  
  // Exibe a senha gerada com os números curtos
  const displayGeneratedPassword = () => {
    if (buttons.length === 0) return <p>Esperando sequência...</p>;
  
    // Filtrando apenas os botões corretos e exibindo os números curtos correspondentes
    return (
      <div>
        <h2>Senha Gerada:</h2>
        <p>
          {password
            .map(pair => {
              // Para cada par, vamos gerar os números curtos correspondentes
              const button = buttons.find(b => b.pair[0] === pair[0] && b.pair[1] === pair[1] && b.isCorrect);
              if (button) {
                return `${button.shortNumbers[0]} ${button.shortNumbers[1]}`;  // Exibe os números curtos correspondentes
              }
              return '';
            })
            .join(' ')}  {/* Junta os números curtos com espaço */}
        </p>
      </div>
    );
  };
  
  

const getUniqueNumberForHash = (hash) => {
  let number = hashToNumberMap[hash];
  
  if (!number) {
    // Se o hash não existe, atribui um novo número (pode ser o próximo número na sequência, por exemplo)
    number = Object.keys(hashToNumberMap).length + 1;
    setHashToNumberMap(prevMap => ({
      ...prevMap,
      [hash]: number
    }));
  }
  
  return number;
};


  // Função para validar a sequência
  const handleValidatePassword = async () => {
    try {
      console.log('Session ID enviado:', sessionId);

      const formattedSequence = password.flat().map((item) => item.toString());
      console.log('Senha do usuário antes do envio:', formattedSequence);

      const data = {
        session_id: sessionId,
        sequence: formattedSequence,
      };

      // Criptografa o sessionId antes de enviar ao backend
      const encryptedSessionId = CryptoJS.AES.encrypt(sessionId, 'chave-secreta').toString();

      const response = await axios.post('http://127.0.0.1:8000/validate_sequence', data, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Encrypted-Session-Id': encryptedSessionId, // Envia sessionId criptografado
        },
      });

      console.log('✅ Sequência validada com sucesso:', response.data);
      toast.success('Sequência validada com sucesso!');
      setIsSessionValid(false);
    } catch (error) {
      console.log('Error response:', error.response ? error.response.data : error.message);
      toast.error('Erro ao validar a sequência');
    }
  };

  // Função para gerar sessão no backend
  const generateSession = async (username) => {
    const response = await axios.post('http://127.0.0.1:8000/generate_session', { username });
    console.log('Resposta da API de geração de sessão:', response.data);
    return response.data;
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>

      <div className="form-container">
        <label htmlFor="username">Nome de Usuário:</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Digite seu nome de usuário"
        />
      </div>

      <button onClick={handleGenerateSession} className="primary-button" disabled={isLoading}>
        {isLoading ? 'Gerando Sessão...' : 'Gerar Nova Sessão'}
      </button>

      {isSessionValid ? (
        <>
          {displayGeneratedPassword()}

          <h2>Clique nos botões abaixo para digitar a senha:</h2>
          <div className="buttons-container">{displayButtons()}</div>

          <h3>Senha Selecionada:</h3>
          <p>{inputSequence.join(' ')}</p>

          <button onClick={handleValidatePassword} className="primary-button">
            Validar Senha
          </button>

          <div className="token-info">
            <h3>Token de Verificação:</h3>
            <p>{token}</p>
          </div>
        </>
      ) : null}

      <ToastContainer />
    </div>
  );
}

export default App;
