// src/App.js
import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [sequence, setSequence] = useState([]);
  const [inputSequence, setInputSequence] = useState([]);
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);

  // Função para gerar nova sessão
  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    try {
      const data = await generateSession(username);
      setSessionId(data.session_id);
      setSequence(data.sequence);
      setToken(data.token);
      setIsSessionValid(true);
      toast.success('Sessão gerada com sucesso!');
    } catch (error) {
      toast.error('Erro ao gerar sessão');
    }
  };

  // Função para enviar a sequência e validar no backend
 // Função para enviar a sequência e validar no backend
 const handleValidateSequence = async () => {
  try {
    // Converte os pares clicados em uma sequência achatada [1, 5, 2, 3] ao invés de [[1, 5], [2, 3]]
    const sequenceToSend = inputSequence.flat();  // Usa flat() para achatar a lista

    console.log("Sequência achatada a ser enviada para o backend:", sequenceToSend);

    // Envia os dados para o backend
    const response = await axios.post('http://127.0.0.1:8000/validate_sequence', {
      session_id: sessionId,  // Verifique se o sessionId está correto
      sequence: sequenceToSend,  // Envia como uma lista achatada de inteiros
    }, {
      headers: {
        Authorization: `Bearer ${token}`,  // Token JWT
      },
    });

    console.log("✅ Sequência validada com sucesso:", response.data);
    toast.success('Sequência validada com sucesso!');
    setIsSessionValid(false);
    setIsButtonDisabled(true); // Desabilita os botões após a validação
  } catch (error) {
    console.log(error);  // Log de erro para debugar
    toast.error('Sequência incorreta');
  }
};


  
  // Função de hash para transformar a combinação em algo único
  const hashCombination = (pair) => {
    // Exemplo de hash simples, você pode usar algo mais seguro se necessário
    return `${pair[0]}-${pair[1]}`; // Pode ser substituído por um hash real
  };
  

  // Função para capturar o clique do botão e formar a sequência
  const handleButtonClick = (index) => {
    if (inputSequence.length < sequence.length) {
      const pair = sequence[index]; // Pega o par da sequência
      setInputSequence(prevSequence => {
        const newSequence = [...prevSequence, pair]; // Adiciona o par ao invés do número
        console.log("Sequência selecionada:", newSequence); // Verifica a sequência
        return newSequence;
      });
    }
  };
  

  // Função para gerar a sessão no backend
  const generateSession = async (username) => {
    const response = await axios.post('http://127.0.0.1:8000/generate_session', { username });
    return response.data;
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>

      <div>
        <label htmlFor="username">Nome de Usuário:</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Digite seu nome de usuário"
        />
      </div>

      <button onClick={handleGenerateSession}>Gerar Nova Sessão</button>

      {isSessionValid && (
        <>
          <h2>Sequência Gerada:</h2>
          <ul>
            {sequence.map((pair, index) => (
              <li key={index}>
                {pair[0]} - {pair[1]}
              </li>
            ))}
          </ul>

          <h2>Clique na sequência:</h2>
          <div className="buttons">
            {sequence.map((_, index) => (
              <button
                key={index}
                onClick={() => handleButtonClick(index + 1)}
                disabled={isButtonDisabled} // Desabilita os botões após a validação
              >
                {index + 1}
              </button>
            ))}
          </div>

          <h3>Sequência Selecionada:</h3>
          <ul>
            {inputSequence.map((num, index) => (
              <li key={index}>{num}</li>
            ))}
          </ul>

          <button onClick={handleValidateSequence}>Validar Sequência</button>
        </>
      )}

      <ToastContainer />
    </div>
  );
}

export default App;
