import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';
import CryptoJS from 'crypto-js';

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [password, setPassword] = useState({ original: [], hashed: [] });
  const [inputSequence, setInputSequence] = useState([]);
  const [incorrectSequence, setIncorrectSequence] = useState([]);
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);
  const [buttons, setButtons] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  const hashToNumber = (input) => {
    if (input === undefined || input === null) {
      console.error("Valor inválido para hash:", input);
      return 0;
    }
    const hash = CryptoJS.SHA256(input.toString()).toString(CryptoJS.enc.Base64);
    let uniqueNumber = 0;
    for (let i = 0; i < hash.length; i++) {
      uniqueNumber += hash.charCodeAt(i);
    }
    return (uniqueNumber % 9) + 1;
  };

  const handleButtonClick = (shortNumber, secondShortNumber) => {
    const flatPassword = password.hashed.flat();
    const nextExpectedNumber = flatPassword[inputSequence.length];

    if (nextExpectedNumber === undefined) {
      return;
    }

    let selectedNumber = null;
    if (shortNumber === nextExpectedNumber) {
      selectedNumber = shortNumber;
    } else if (secondShortNumber === nextExpectedNumber) {
      selectedNumber = secondShortNumber;
    }

    if (selectedNumber !== null) {
      setInputSequence((prevSequence) => [...prevSequence, selectedNumber]);
    } else {
      setIncorrectSequence((prevIncorrect) => [...prevIncorrect, shortNumber]);
      toast.warning("Número incorreto! Você pode enviar a sequência mesmo assim.");
    }
  };

  const handleValidateIncorrectPassword = async () => {
    if (incorrectSequence.length === 0) return;
    toast.info("Enviando sequência incorreta...");
    console.log("Sequência incorreta enviada:", incorrectSequence);
  };

  return (
    <div className="App wrapper">
      <h1>Teclado Virtual</h1>

      <div className="buttons-container">
        {buttons.map((button, index) => (
          <button
            key={`${button.shortNumber}-${index}`}
            onClick={() => handleButtonClick(button.shortNumber, button.secondShortNumber)}
            disabled={isButtonDisabled}
            className="button"
          >
            {button.secondShortNumber ? `${button.shortNumber} ou ${button.secondShortNumber}` : button.shortNumber}
          </button>
        ))}
      </div>

      <h3>Senha Selecionada:</h3>
      <p className='senha-digitada'>{inputSequence.join(' ')}</p>

      {incorrectSequence.length > 0 && (
        <button onClick={handleValidateIncorrectPassword} className="secondary-button">
          Enviar Sequência Incorreta
        </button>
      )}

      <ToastContainer />
    </div>
  );
}

export default App;
