import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [password, setPassword] = useState([]); // Senha gerada
  const [inputPassword, setInputPassword] = useState([]); // Senha digitada
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);

  // Função para gerar nova sessão
  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    try {
      const data = await generateSession(username);
      console.log('Dados recebidos da API:', data);

      if (data && data.sequence && Array.isArray(data.sequence)) {
        setSessionId(data.session_id);
        setPassword(data.sequence); // Guardando a senha
        setToken(data.token);
        setIsSessionValid(true);

        console.log("Token de Verificação:", data.token);

        toast.success('Sessão gerada com sucesso!');
      } else {
        toast.error('Erro: a sequência não foi retornada corretamente.');
      }
    } catch (error) {
      toast.error('Erro ao gerar sessão');
      console.error('Erro ao gerar sessão:', error);
    }
  };

  // Função para enviar a senha e validar no backend
const handleValidatePassword = async () => {
  try {
    console.log("Senha do usuário antes do envio:", inputSequence);  // Verify sequence format

    // Ensure the sequence is a flat array before sending
    const response = await axios.post('http://127.0.0.1:8000/validate_sequence', {
      session_id: sessionId,
      sequence: inputSequence,  // Directly send the flat sequence
    }, {
      headers: {
        Authorization: `Bearer ${token}`,  // Include token for authentication
      },
    });

    console.log("✅ Sequência validada com sucesso:", response.data);
    toast.success('Sequência validada com sucesso!');
    setIsSessionValid(false);
    setIsButtonDisabled(true);  // Disable buttons after validation
  } catch (error) {
    console.log(error.response ? error.response.data : error.message);  // Log error details
    toast.error('Sequência incorreta');
  }
};


  // Função para gerar a sessão no backend
  const generateSession = async (username) => {
    const response = await axios.post('http://127.0.0.1:8000/generate_session', { username });
    console.log('Resposta da API de geração de sessão:', response.data);
    return response.data;
  };

  // Função para lidar com o clique nos botões da sequência
  const handleButtonClick = (number) => {
    setInputPassword((prevPassword) => {
      const newPassword = [...prevPassword, number];
      console.log("Senha do usuário após clique:", newPassword);
      return newPassword;
    });
  };

  // Verificação do estado da sequência
  useEffect(() => {
    if (Array.isArray(password) && password.length > 0) {
      console.log('Senha carregada:', password);
    }
  }, [password]);

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>

      <div>
        <label htmlFor="username">Nome de Usuário:</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Digite seu nome de usuário"
        />
      </div>

      <button onClick={handleGenerateSession}>Gerar Nova Sessão</button>

      {isSessionValid ? (
        <>
          <h2>Senha Gerada:</h2>
          {password.length === 0 ? (
            <p>Carregando senha...</p>
          ) : (
            <p>{password.join(' ')}</p> // Exibe a senha gerada
          )}

          <h2>Clique nos números abaixo para digitar a senha:</h2>
          {password.length === 0 ? (
            <p>Esperando sequência...</p>
          ) : (
            <div className="buttons">
              {Array.from({ length: 9 }, (_, index) => (
                <button
                  key={index}
                  onClick={() => handleButtonClick(index + 1)}
                  disabled={isButtonDisabled} // Desabilita os botões após a validação
                >
                  {index + 1}
                </button>
              ))}
            </div>
          )}

          <h3>Senha Selecionada:</h3>
          <ul>
            {inputPassword.length === 0 ? (
              <p>Nenhuma sequência selecionada ainda...</p>
            ) : (
              inputPassword.map((num, index) => (
                <li key={index}>{num}</li>
              ))
            )}
          </ul>

          <button onClick={handleValidatePassword}>Validar Senha</button>

          {/* Exibe o Token de Verificação */}
          <div>
            <h3>Token de Verificação:</h3>
            <p>{token}</p>
          </div>
        </>
      ) : null}

      <ToastContainer />
    </div>
  );
}

export default App;
