import React, { useState } from 'react';
import { generateSession, validateSequence, invalidateSession } from './api.js';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import CryptoJS from 'crypto-js';
import axios from 'axios';

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [sequence, setSequence] = useState([]);
  const [inputSequence, setInputSequence] = useState([]);
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);

  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    try {
      const data = await generateSession(username);
      setSessionId(data.session_id);
      setSequence(data.sequence);
      setToken(data.token);
      setIsSessionValid(true);
      setInputSequence([]);
      toast.success('Sessão gerada com sucesso!');
    } catch (error) {
      toast.error('Erro ao gerar sessão');
    }
  };

  const handleValidateSequence = async () => {
    console.log("Sessão ID:", sessionId);
    console.log("Token:", token);
    console.log("Sequência enviada:", inputSequence);

    try {
      const formattedSequence = inputSequence.map(index => sequence[index - 1]);

      const response = await axios.post('http://127.0.0.1:8000/validate_sequence', {
        session_id: sessionId,
        sequence: formattedSequence,
      }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      console.log("✅ Sequência validada com sucesso:", response.data);
      toast.success('Sequência validada com sucesso!');
      setIsSessionValid(false);
    } catch (error) {
      if (error.response) {
        console.error("❌ Erro ao validar sequência:", error.response.data);
        toast.error('Sequência incorreta');
      } else if (error.request) {
        console.error("❌ Erro ao conectar com o servidor:", error.request);
        toast.error('Erro ao conectar com o servidor');
      } else {
        console.error("❌ Erro desconhecido:", error.message);
        toast.error('Erro desconhecido');
      }
    }
  };

  const handleButtonClick = (number) => {
    if (inputSequence.length < 5) {
      setInputSequence([...inputSequence, number]);
    }
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>
      <div>
        <label htmlFor="username">Nome de Usuário:</label>
        <input 
          id="username" 
          type="text" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
          placeholder="Digite seu nome de usuário"
        />
      </div>

      <button onClick={handleGenerateSession}>Gerar Nova Sessão</button>

      {isSessionValid && (
        <>
          <h2>Clique na sequência:</h2>
          <div className="buttons-grid">
            {[1, 2, 3, 4, 5, 6, 7, 8, 9].map((num) => (
              <button key={num} onClick={() => handleButtonClick(num)}>
                {num}
              </button>
            ))}
          </div>

          <h3>Sequência Selecionada:</h3>
          <ul>
            {inputSequence.map((num, index) => (
              <li key={index}>{num}</li>
            ))}
          </ul>

          <button onClick={handleValidateSequence}>Validar Sequência</button>
        </>
      )}

      <button onClick={handleInvalidateSession}>Invalidar Sessão</button>

      <ToastContainer />
    </div>
  );
}

export default App;
