import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [sessionId, setSessionId] = useState('');
  const [buttons, setButtons] = useState([]);
  const [token, setToken] = useState('');
  const [inputSequence, setInputSequence] = useState([]);

  const generateSession = async () => {
    try {
      const response = await axios.post('http://127.0.0.1:8000/generate_session');
      console.log('Dados da sessão gerada:', response.data); // Log dos dados recebidos
      setSessionId(response.data.session_id);
      setButtons(response.data.sequence);
      setToken(response.data.token);
      setInputSequence([]); // Limpar a sequência ao gerar nova sessão
    } catch (error) {
      console.error('Erro ao gerar sessão:', error);
      alert('Erro ao gerar sessão. Verifique a conexão com o servidor.');
    }
  };

  const handleButtonClick = (pair) => {
    // Captura os dois números do par, mas apenas adiciona um número de cada vez
    setInputSequence((prevSequence) => {
      const newSequence = [...prevSequence, pair[0], pair[1]]; // Adiciona ambos os números do par
      console.log('Sequência de entrada atualizada:', newSequence); // Log da sequência
      return newSequence;
    });
  };

  const validateSequence = async () => {
    if (!sessionId || inputSequence.length === 0) {
      alert('Sessão inválida ou sequência vazia.');
      return;
    }

    const senhaCorreta = [1, 2, 3, 4, 5]; // Senha esperada
    let sequenceToSend = []; // Array para armazenar os números válidos

    // Log da sequência de entrada
    console.log('Sequência de entrada antes da validação:', inputSequence);

    // Loop para verificar cada número da sequência de entrada
  // Loop para verificar cada número da sequência de entrada
for (let i = 0; i < inputSequence.length; i++) {
  let buttonMatched = false;

  // Verificar se o número da sequência corresponde à senha na posição i
  if (senhaCorreta[i] === inputSequence[i][0] || senhaCorreta[i] === inputSequence[i][1]) {
    // Evitar duplicação de números na sequência
    if (!sequenceToSend.includes(inputSequence[i])) {
      sequenceToSend.push(inputSequence[i]); // Adiciona à sequência final se válido
    }
    buttonMatched = true;
  }

  // Caso o número da sequência não corresponda a nenhum botão, vai para o próximo conjunto
  if (!buttonMatched) {
    continue;
  }
}


    // Log da sequência final antes de enviar
    console.log('Sequência final a ser enviada para o banco de dados:', sequenceToSend);

    // Se a sequência gerada estiver correta, envia para o servidor
    if (sequenceToSend.length === senhaCorreta.length) {
      console.log('Dados enviados para validação:', { session_id: sessionId, sequence: sequenceToSend });

      try {
        const response = await axios.post(
          'http://127.0.0.1:8000/validate_sequence',
          { session_id: sessionId, sequence: sequenceToSend },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        console.log('Resposta da validação:', response.data); // Log da resposta da validação
        alert(response.data.message);
      } catch (error) {
        console.error('Erro na validação:', error);
        alert(error.response?.data?.detail || 'Erro na validação da sequência.');
      }
    } else {
      alert('Sequência inválida! Não corresponde à senha esperada.');
    }
  };

  return (
    <div>
      <button onClick={generateSession}>Gerar Sessão</button>
      <div>
        {buttons.map((pair, index) => (
          <button key={index} onClick={() => handleButtonClick(pair)}>
            [{pair[0]}, {pair[1]}]
          </button>
        ))}
      </div>
      <button onClick={validateSequence}>Validar</button>
    </div>
  );
}

export default App;
