import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [pairs, setPairs] = useState([]); // Armazena os pares gerados pelo backend
  const [responses, setResponses] = useState([]); // Armazena as respostas do usuário (1 ou 2)
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);

  // Função para gerar nova sessão
  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    try {
      const response = await axios.post('http://127.0.0.1:8000/generate_session', { 
        username 
      });
      
      console.log('Dados recebidos da API:', response.data);

      if (response.data && response.data.pairs && Array.isArray(response.data.pairs)) {
        setSessionId(response.data.session_id);
        setPairs(response.data.pairs);
        setToken(response.data.token);
        // Inicializa o array de respostas com null para cada par
        setResponses(new Array(response.data.pairs.length).fill(null));
        setIsSessionValid(true);
        toast.success('Sessão gerada com sucesso!');
      } else {
        toast.error('Erro: os pares não foram retornados corretamente.');
      }
    } catch (error) {
      toast.error('Erro ao gerar sessão');
      console.error('Erro ao gerar sessão:', error);
    }
  };

  // Função para registrar a escolha do usuário (1 para o primeiro número, 2 para o segundo)
  const handleButtonChoice = (pairIndex, choicePosition) => {
    const newResponses = [...responses];
    newResponses[pairIndex] = choicePosition;
    setResponses(newResponses);
  };

  // Função para enviar a sequência e validar no backend
  const handleValidateSequence = async () => {
    // Verifica se todas as posições foram preenchidas
    if (responses.some(response => response === null)) {
      toast.warning('Por favor, selecione uma opção para cada dígito.');
      return;
    }

    try {
      console.log("Enviando respostas:", responses);
      console.log("Session ID:", sessionId);
      
      const response = await axios.post('http://127.0.0.1:8000/validate_sequence', {
        session_id: sessionId,
        responses: responses  // Formato correto: [1, 2, 1, 2] (exemplo)
      }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
  
      console.log("✅ Sequência validada com sucesso:", response.data);
      toast.success('Sequência validada com sucesso!');
      setIsSessionValid(false);
      resetForm();
    } catch (error) {
      console.log(error.response ? error.response.data : error.message);
      toast.error(error.response?.data?.detail || 'Sequência incorreta');
      // Opcional: resetar as respostas após erro
      setResponses(new Array(pairs.length).fill(null));
    }
  };

  const resetForm = () => {
    setSessionId('');
    setPairs([]);
    setResponses([]);
    setToken('');
    setIsSessionValid(false);
  };

  return (
    <div className="App">
      <h1>Teclado Virtual Seguro</h1>

      {!isSessionValid ? (
        <div className="login-form">
          <div>
            <label htmlFor="username">Nome de Usuário:</label>
            <input
              id="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Digite seu nome de usuário"
            />
          </div>
          <button onClick={handleGenerateSession}>Gerar Nova Sessão</button>
        </div>
      ) : (
        <div className="keypad-container">
          <h2>Digite sua senha</h2>
          <p>Para cada dígito da sua senha, selecione o botão que contém o número correto</p>
          
          <div className="keypad">
            {pairs.map((pair, index) => (
              <div key={index} className="pair-row">
                <span className="digit-label">Dígito {index + 1}:</span>
                <div className="button-pair">
                  <button 
                    className={responses[index] === 1 ? 'selected' : ''}
                    onClick={() => handleButtonChoice(index, 1)}
                  >
                    {pair[0]}
                  </button>
                  <button 
                    className={responses[index] === 2 ? 'selected' : ''}
                    onClick={() => handleButtonChoice(index, 2)}
                  >
                    {pair[1]}
                  </button>
                </div>
              </div>
            ))}
          </div>
          
          <div className="actions">
            <button onClick={handleValidateSequence}>Validar Sequência</button>
            <button onClick={resetForm}>Cancelar</button>
          </div>

          {/* Área de debug - pode remover em produção */}
          <div className="debug-area">
            <h3>Informações de Debug:</h3>
            <p>Token: {token.substring(0, 15)}...</p>
            <p>Respostas selecionadas: {JSON.stringify(responses)}</p>
          </div>
        </div>
      )}

      <ToastContainer />
    </div>
  );
}

export default App;
