// src/App.js
import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [sequence, setSequence] = useState([]);
  const [inputSequence, setInputSequence] = useState([]);
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Função para gerar nova sessão
  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    try {
      const data = await generateSession(username);
      setSessionId(data.session_id);
      setSequence(data.pairs); // o backend retorna os pares na propriedade "pairs"
      setToken(data.token);
      setIsSessionValid(true);
      setCurrentIndex(0);
      setInputSequence([]);

      console.log("Token de Verificação:", data.token);
      toast.success('Sessão gerada com sucesso!');
    } catch (error) {
      toast.error('Erro ao gerar sessão');
    }
  };

  // Função para enviar a sequência e validar no backend
  function handleValidateSequence() {
    console.log("Sequência a ser enviada para o backend:", inputSequence);
    
    axios
      .post(
        "http://127.0.0.1:8000/validate_sequence",
        {
          session_id: sessionId,
          responses: inputSequence
        },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      )
      .then(response => {
        console.log("Resposta:", response.data);
        toast.success("Sequência validada com sucesso!");
      })
      .catch(error => {
        console.error("Erro:", error);
        toast.error("Sequência incorreta ou erro na validação.");
      });
  }
  
  // Função para capturar o clique em um dos botões do par
  // selection: 1 para o dígito à esquerda, 2 para o dígito à direita
  const handlePairSelection = (selection) => {
    if (currentIndex < sequence.length) {
      setInputSequence(prev => [...prev, selection]);
      setCurrentIndex(currentIndex + 1);
    }
  };

  // Função para gerar a sessão no backend
  const generateSession = async (username) => {
    const response = await axios.post('http://127.0.0.1:8000/generate_session', { username });
    return response.data;
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>

      <div>
        <label htmlFor="username">Nome de Usuário:</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Digite seu nome de usuário"
        />
      </div>

      <button onClick={handleGenerateSession}>Gerar Nova Sessão</button>

      {isSessionValid && (
        <>
          <h2>Teclado Virtual</h2>
          {currentIndex < sequence.length ? (
            <div>
              <h3>Digite o dígito {currentIndex + 1} da sua senha:</h3>
              <div className="pair-container">
                <button onClick={() => handlePairSelection(1)}>
                  {sequence[currentIndex][0]}
                </button>
                <button onClick={() => handlePairSelection(2)}>
                  {sequence[currentIndex][1]}
                </button>
              </div>
            </div>
          ) : (
            <div>
              <h3>Sequência Selecionada:</h3>
              <ul>
                {inputSequence.map((num, index) => (
                  <li key={index}>{num}</li>
                ))}
              </ul>
              <button onClick={handleValidateSequence}>Validar Sequência</button>
            </div>
          )}

          {/* Exibe o Token de Verificação */}
          <div>
            <h3>Token de Verificação:</h3>
            <p>{token}</p>
          </div>
        </>
      )}

      <ToastContainer />
    </div>
  );
}

export default App;
