import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [sessionId, setSessionId] = useState('');
  const [passwordSequence, setPasswordSequence] = useState([]);
  const [inputSequence, setInputSequence] = useState([]);

  const handleGenerateSession = async () => {
    try {
      const response = await axios.post("http://127.0.0.1:8000/generate_session");
      console.log("Resposta da API:", response.data);
      setSessionId(response.data.session_id);
      setPasswordSequence(response.data.sequence);
    } catch (error) {
      console.error("Erro ao gerar sessão:", error);
    }
  };

  const handleButtonClick = (number) => {
    setInputSequence([...inputSequence, number]);
    console.log("Sequência digitada:", [...inputSequence, number]);
  };

  const handleValidateSequence = async () => {
    try {
      console.log("Enviando para validação:", { session_id: sessionId, sequence: inputSequence });

      const response = await axios.post("http://127.0.0.1:8000/validate_sequence", {
        session_id: sessionId,
        sequence: inputSequence,
      });

      console.log("Resposta da API:", response.data);
      alert("Senha correta!");
    } catch (error) {
      console.error("Erro na validação:", error.response?.data || error);
      alert("Senha incorreta!");
    }
  };

  return (
    <div>
      <h1>Teclado Virtual</h1>
      <button onClick={handleGenerateSession}>Gerar Sessão</button>

      {passwordSequence.length > 0 && (
        <div>
          <h2>Selecione os números na ordem correta:</h2>
          {passwordSequence.map((num, index) => (
            <button key={index} onClick={() => handleButtonClick(num)}>
              {num}
            </button>
          ))}
        </div>
      )}

      {inputSequence.length > 0 && (
        <div>
          <h2>Sua sequência:</h2>
          <p>{inputSequence.join(" - ")}</p>
          <button onClick={handleValidateSequence}>Validar Senha</button>
        </div>
      )}
    </div>
  );
}

export default App;
