import React, { useState } from 'react';
import { generateSession, validateSequence, invalidateSession } from './api.js';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function App() {
  const [username, setUsername] = useState('');  // Adicionando o estado para o nome de usuário
  const [sessionId, setSessionId] = useState('');
  const [sequence, setSequence] = useState([]);
  const [inputSequence, setInputSequence] = useState([]);
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);

  // Gera uma nova sessão, agora passando o username
  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    try {
      const data = await generateSession(username);  // Passando username para a função
      setSessionId(data.session_id);
      setSequence(data.sequence);
      setToken(data.token);
      setIsSessionValid(true);
      toast.success('Sessão gerada com sucesso!');
    } catch (error) {
      toast.error('Erro ao gerar sessão');
    }
  };

  // Valida a sequência fornecida
  const handleValidateSequence = async () => {
    try {
      // Usando a função validateSequence importada de api.js
      await validateSequence(sessionId, inputSequence, token);
      toast.success('Sequência validada com sucesso!');
      setIsSessionValid(false);  // Finaliza a sessão após validação
    } catch (error) {
      toast.error('Sequência incorreta');
    }
  };

  // Invalidar a sessão
  const handleInvalidateSession = async () => {
    try {
      await invalidateSession(sessionId);
      toast.success('Sessão invalidada!');
      setIsSessionValid(false);
    } catch (error) {
      toast.error('Erro ao invalidar sessão');
    }
  };

  // Função para adicionar um número à sequência de entrada
  const handleButtonClick = (number) => {
    if (inputSequence.length < sequence.length) {
      setInputSequence([...inputSequence, number]);
    }
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>
      
      {/* Campo para inserir o nome de usuário */}
      <div>
        <label htmlFor="username">Nome de Usuário:</label>
        <input 
          id="username" 
          type="text" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
          placeholder="Digite seu nome de usuário"
        />
      </div>

      <button onClick={handleGenerateSession}>Gerar Nova Sessão</button>

      {isSessionValid && (
        <>
          <h2>Sequência Gerada:</h2>
          <ul>
            {sequence.map((pair, index) => (
              <li key={index}>
                {pair[0]} - {pair[1]}
              </li>
            ))}
          </ul>

          <h2>Clique na sequência:</h2>
          <div className="buttons">
            {/* Adicionando botões numerados */}
            {sequence.map((_, index) => (
              <button
                key={index}
                onClick={() => handleButtonClick(index + 1)}
              >
                {index + 1}
              </button>
            ))}
          </div>

          <h3>Sequência Selecionada:</h3>
          <ul>
            {inputSequence.map((num, index) => (
              <li key={index}>{num}</li>
            ))}
          </ul>

          <button onClick={handleValidateSequence}>Validar Sequência</button>
        </>
      )}

      <button onClick={handleInvalidateSession}>Invalidar Sessão</button>
      
      <ToastContainer />
    </div>
  );
}

export default App;
