import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';
import CryptoJS from 'crypto-js'; // Importe a biblioteca

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [password, setPassword] = useState([]);
  const [inputSequence, setInputSequence] = useState([]);
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);
  const [buttons, setButtons] = useState([]);
  const [isLoading, setIsLoading] = useState(false); // Para controle de loading

  // Função para gerar hash para cada número individualmente
  const hashToNumber = (input) => {
    if (input === undefined || input === null) {
      console.error("Valor inválido para hash:", input);
      return 0; // Retorna 0 ou outro valor padrão caso o input seja inválido
    }

    const hash = CryptoJS.SHA256(input.toString()).toString(CryptoJS.enc.Base64); // Base64 para garantir um valor único
    let uniqueNumber = 0;
    for (let i = 0; i < hash.length; i++) {
      uniqueNumber += hash.charCodeAt(i); // Soma os códigos dos caracteres
    }

    // Garante que o número gerado seja entre 0 e 9
    return uniqueNumber % 10;
  };

  // Função para gerar nova sessão
  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    setIsLoading(true); // Ativa o loading

    try {
      const data = await generateSession(username);
      console.log('Dados recebidos da API:', data);

      if (data && data.sequence && Array.isArray(data.sequence)) {
        setSessionId(data.session_id);
        setPassword(formatSequence(data.sequence)); // Guardando a senha formatada
        setToken(data.token);
        setIsSessionValid(true);
        generateButtons(data.sequence); // Gera botões misturados

        console.log('Token de Verificação:', data.token);
        toast.success('Sessão gerada com sucesso!');
      } else {
        toast.error('Erro: a sequência não foi retornada corretamente.');
      }
    } catch (error) {
      toast.error('Erro ao gerar sessão');
      console.error('Erro ao gerar sessão:', error);
    } finally {
      setIsLoading(false); // Desativa o loading
    }
  };

  // Função para formatar a sequência em pares
  const formatSequence = (sequence) => {
    let formatted = [];
    for (let i = 0; i < sequence.length; i += 2) {
      formatted.push([sequence[i], sequence[i + 1]]);
    }
    return formatted;
  };

  // Função para gerar os botões (agora com hash gerado por número individual)
  const generateButtons = (sequence) => {
    const uniqueNumbers = [...new Set(sequence)]; // Usando Set para garantir números únicos
  
    let allButtons = [];
    let generatedHashes = new Set(); // Para rastrear os hashes já gerados
  
    uniqueNumbers.forEach((num) => {
      let shortNumber = hashToNumber(num); // Gera o hash inicial para o número
      let secondShortNumber = hashToNumber(CryptoJS.SHA256((num + 1).toString()).toString(CryptoJS.enc.Base64)); // Gera o segundo hash
  
      // Verifica se o shortNumber ou secondShortNumber já existe
      if (generatedHashes.has(shortNumber)) {
        // Se já existir, gera um novo hash para shortNumber
        shortNumber = generateUniqueHash(generatedHashes);
      }
  
      if (generatedHashes.has(secondShortNumber)) {
        // Se já existir, gera um novo hash para secondShortNumber
        secondShortNumber = generateUniqueHash(generatedHashes);
      }
  
      // Marca os hashes gerados como usados
      generatedHashes.add(shortNumber);
      generatedHashes.add(secondShortNumber);
  
      allButtons.push({
        num,
        shortNumber,
        secondShortNumber,
      });
  
      console.log(`Gerando botão: ${num}, shortNumber: ${shortNumber}, secondShortNumber: ${secondShortNumber}`);
    });
  
    // Embaralha a ordem dos botões (somente os corretos)
    allButtons = allButtons.sort(() => Math.random() - 0.5);
  
    // Agora, todos os números são clicáveis, independentemente da posição
    allButtons = allButtons.map((button) => {
      button.isClickable = true; // Todos os números são clicáveis agora
      return button;
    });
  
    setButtons(allButtons); // Atualiza os botões no estado
  };
  
  // Função para gerar um hash único
  const generateUniqueHash = (existingHashes) => {
    let newHash;
    do {
      newHash = Math.floor(Math.random() * 100); // Gera um número aleatório
    } while (existingHashes.has(newHash)); // Verifica se já existe esse hash
  
    return newHash;
  };
  
  // Exibe os botões misturados
  const displayButtons = () => {
    if (buttons.length === 0) return <p>Esperando sequência...</p>;

    return (
      <div className="buttons">
        {buttons.map((button) => (
          <button
            key={button.num}
            onClick={() => handleButtonClick(button.num)}
            disabled={isButtonDisabled}
            className="button"
            style={inputSequence.includes(button.num) ? { backgroundColor: 'green' } : {}}
          >
            {button.secondShortNumber} ou {button.shortNumber} {/* Exibe ambos os números */}
          </button>
        ))}
      </div>
    );
  };

  // Função para lidar com o clique nos botões
  const handleButtonClick = (pair) => {
    const flatPassword = password.flat(); // "Achata" a senha para facilitar a comparação
    const nextExpectedNumber = flatPassword[inputSequence.length]; // Obtém o próximo número esperado da senha

    // Verifica se há um próximo número esperado (caso a senha já tenha sido totalmente inserida)
    if (nextExpectedNumber === undefined) {
      toast.error('A senha já foi completamente inserida!');
      return;
    }

    // Verifica se ambos os números do par são válidos em relação à sequência
    const isValidFirstNumber = pair === nextExpectedNumber;
    const isValidSecondNumber = pair === nextExpectedNumber + 1;

    // Se qualquer um dos dois números do par for válido, prossegue
    if (isValidFirstNumber || isValidSecondNumber) {
      setInputSequence((prevSequence) => {
        const newSequence = [...prevSequence, pair];
        console.log('Sequência do usuário após clique:', newSequence);

        // Verifica se a sequência está completa e corresponde à senha
        if (newSequence.length === flatPassword.length) {
          const isCorrect = newSequence.every((num, index) => num === flatPassword[index]);
          if (isCorrect) {
            toast.success('Senha correta! Acesso liberado.');
          } else {
            toast.error('Sequência incorreta. Tente novamente.');
            setIsButtonDisabled(true);
            setTimeout(() => setIsButtonDisabled(false), 2000);
          }
        }

        return newSequence;
      });
    } else {
      toast.error(`Par incorreto. O próximo número esperado é ${nextExpectedNumber}. Tente novamente.`);
      setIsButtonDisabled(true); // Desabilitar todos os botões após erro
      setTimeout(() => setIsButtonDisabled(false), 2000); // Habilitar novamente após 2 segundos
    }
  };

  // Exibe a senha gerada com os números curtos
  const displayGeneratedPassword = () => {
    if (password.length === 0) return <p>Esperando sequência...</p>;

    let generatedPassword = [];
    password.forEach(pair => {
      pair.forEach(num => {
        const shortNumber = hashToNumber(num); // Gerando o número curto para cada número da senha
        generatedPassword.push(shortNumber);
      });
    });

    return (
      <div>
        <h2>Senha Gerada:</h2>
        <p>{`[${generatedPassword.join(', ')}]`}</p>  {/* Exibe o array plano como uma string */}
      </div>
    );
  };

  // Função para validar a sequência
  const handleValidatePassword = async () => {
    try {
      console.log('Session ID enviado:', sessionId);

      const formattedSequence = password.flat().map((item) => item.toString());
      console.log('Senha do usuário antes do envio:', formattedSequence);

      // Comparando a sequência do usuário com a senha correta
      if (inputSequence.join(' ') !== formattedSequence.join(' ')) {
        toast.error('A sequência digitada está incorreta. Tente novamente.');
        return;
      }

      const data = {
        session_id: sessionId,
        sequence: formattedSequence,
      };

      // Criptografa o sessionId antes de enviar ao backend
      const encryptedSessionId = CryptoJS.AES.encrypt(sessionId, 'chave-secreta').toString();

      const response = await axios.post('http://127.0.0.1:8000/validate_sequence', data, {
        headers: {
          Authorization: `Bearer ${token}`,
          'Encrypted-Session-Id': encryptedSessionId, // Envia sessionId criptografado
        },
      });

      console.log('✅ Sequência validada com sucesso:', response.data);
      toast.success('Sequência validada com sucesso!');
      setIsSessionValid(false);
    } catch (error) {
      console.log('Error response:', error.response ? error.response.data : error.message);
      toast.error('Erro ao validar a sequência');
    }
  };

  // Função para gerar sessão no backend
  const generateSession = async (username) => {
    const response = await axios.post('http://127.0.0.1:8000/generate_session', { username });
    console.log('Resposta da API de geração de sessão:', response.data);
    return response.data;
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>

      <div className="form-container">
        <label htmlFor="username">Nome de Usuário:</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Digite seu nome de usuário"
        />
      </div>

      <button onClick={handleGenerateSession} className="primary-button" disabled={isLoading}>
        {isLoading ? 'Gerando Sessão...' : 'Gerar Nova Sessão'}
      </button>

      {isSessionValid ? (
        <>
          {displayGeneratedPassword()}

          <h2>Clique nos botões abaixo para digitar a senha:</h2>
          <div className="buttons-container">{displayButtons()}</div>

          <h3>Senha Selecionada:</h3>
          <p>{inputSequence.join(' ')}</p>

          <button onClick={handleValidatePassword} className="primary-button">
            Validar Senha
          </button>
        </>
      ) : null}

      <ToastContainer />
    </div>
  );
}

export default App;
