import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import crypto from 'crypto';
import './api.css';

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [hashes, setHashes] = useState([]);
  const [hashNumbers, setHashNumbers] = useState([]);
  const [inputSequence, setInputSequence] = useState([]);
  const [token, setToken] = useState('');
  const [isSessionValid, setIsSessionValid] = useState(false);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);
  const [buttons, setButtons] = useState([]);

  const handleGenerateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário.');
      return;
    }

    try {
      const data = await generateSession(username);
      console.log('Dados recebidos da API:', data);

      if (data && Array.isArray(data.sequence)) {
        setSessionId(data.session_id);
        setToken(data.token);
        setIsSessionValid(true);
        generateHashes(data.sequence.length);
        toast.success('Sessão gerada com sucesso!');
      } else {
        toast.error('Erro: a sequência não foi retornada corretamente.');
      }
    } catch (error) {
      toast.error('Erro ao gerar sessão');
      console.error('Erro ao gerar sessão:', error);
    }
  };

  const generateHashes = (length) => {
    let generatedHashes = [];
    let hashMap = [];
    
    for (let i = 0; i < length; i++) {
      const hash = crypto.randomBytes(4).toString('hex');
      generatedHashes.push(hash);
      hashMap.push(i + 1); // Número exclusivo para cada hash
    }
    
    setHashes(generatedHashes);
    setHashNumbers(hashMap);
    generateButtons(hashMap);
  };

  const generateButtons = (hashMap) => {
    let buttonPairs = hashMap.map(num => [num, num + 1]);
    let shuffledButtons = buttonPairs.sort(() => Math.random() - 0.5);
    setButtons(shuffledButtons);
  };

  const handleButtonClick = (pair) => {
    setInputSequence(prevSequence => [...prevSequence, pair[0]]);
  };

  const displayButtons = () => {
    if (buttons.length === 0) return <p>Esperando sequência...</p>;
    return (
      <div className="buttons">
        {buttons.map((pair, index) => (
          <button key={index} onClick={() => handleButtonClick(pair)} disabled={isButtonDisabled}>
            [{pair[0]} ou {pair[1]}]
          </button>
        ))}
      </div>
    );
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>

      <div>
        <label htmlFor="username">Nome de Usuário:</label>
        <input
          id="username"
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Digite seu nome de usuário"
        />
      </div>

      <button onClick={handleGenerateSession}>Gerar Nova Sessão</button>

      {isSessionValid ? (
        <>
          <h2>Hashes Gerados:</h2>
          <p>{hashes.join(' | ')}</p>

          <h2>Clique nos botões abaixo:</h2>
          {displayButtons()}

          <h3>Sequência Selecionada:</h3>
          <p>{inputSequence.join(' - ') || "Nenhuma sequência selecionada ainda..."}</p>
        </>
      ) : null}

      <ToastContainer />
    </div>
  );
}

export default App;
