import React, { useState } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';
import './api.css';

function App() {
  const [username, setUsername] = useState('');
  const [sessionId, setSessionId] = useState('');
  const [token, setToken] = useState('');
  const [pairs, setPairs] = useState([]);
  const [responses, setResponses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [step, setStep] = useState('login'); // login, keypad, success

  const API_URL = 'http://192.168.1.6:8000';

  const generateSession = async () => {
    if (!username) {
      toast.error('Por favor, insira um nome de usuário');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post(`${API_URL}/generate_session`, {
        username: username
      });

      setSessionId(response.data.session_id);
      setPairs(response.data.pairs);
      setToken(response.data.token);
      setResponses(new Array(response.data.pairs.length).fill(null));
      setStep('keypad');
    } catch (error) {
      toast.error(error.response?.data?.detail || 'Erro ao gerar sessão');
    } finally {
      setLoading(false);
    }
  };

  const handleKeypadClick = (pairIndex, buttonPosition) => {
    const newResponses = [...responses];
    // Em vez de salvar como par, salva apenas o número selecionado
    newResponses[pairIndex] = pairs[pairIndex][buttonPosition - 1]; // Corrige o índice
    setResponses(newResponses);
  };

  const validateSequence = async () => {
    if (responses.includes(null)) {
      toast.error('Por favor, complete a sequência');
      return;
    }

    setLoading(true);
    try {
      await axios.post(
        `${API_URL}/validate_sequence`,
        {
          session_id: sessionId,
          responses: responses  // Agora é um array de números
        },
        {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      );

      toast.success('Autenticação bem-sucedida!');
      setStep('success');
    } catch (error) {
      toast.error(error.response?.data?.detail || 'Sequência incorreta');
      // Resetar respostas em caso de erro
      setResponses(new Array(pairs.length).fill(null));
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setUsername('');
    setSessionId('');
    setToken('');
    setPairs([]);
    setResponses([]);
    setStep('login');
  };

  return (
    <div className="app-container">
      <ToastContainer position="top-center" />
      <h1>Sistema de Autenticação Segura</h1>

      {step === 'login' && (
        <div className="login-form">
          <h2>Login</h2>
          <input
            type="text"
            placeholder="Nome de usuário"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <button onClick={generateSession} disabled={loading}>
            {loading ? 'Carregando...' : 'Continuar'}
          </button>
        </div>
      )}

      {step === 'keypad' && (
        <div className="keypad-container">
          <h2>Digite sua senha</h2>
          <p>Selecione o botão que contém o dígito correto da sua senha</p>
          
          <div className="keypad">
            {pairs.map((pair, index) => (
              <div key={index} className="keypad-row">
                <span className="digit-label">Dígito {index + 1}:</span>
                <div className="button-container">
                  <button
                    className={responses[index] === pair[0] ? 'selected' : ''}
                    onClick={() => handleKeypadClick(index, 1)}
                  >
                    {pair[0]}
                  </button>
                  <button
                    className={responses[index] === pair[1] ? 'selected' : ''}
                    onClick={() => handleKeypadClick(index, 2)}
                  >
                    {pair[1]}
                  </button>
                </div>
              </div>
            ))}
          </div>
          
          <div className="action-buttons">
            <button onClick={validateSequence} disabled={loading}>
              {loading ? 'Validando...' : 'Validar'}
            </button>
            <button onClick={resetForm}>Cancelar</button>
          </div>
        </div>
      )}

      {step === 'success' && (
        <div className="success-container">
          <h2>Login bem-sucedido!</h2>
          <p>Você está autenticado.</p>
          <button onClick={resetForm}>Sair</button>
        </div>
      )}
    </div>
  );
}

export default App;
